    function listArray(book) {
        var h4 = document.createElement("h4");
        h4.classList.add("bookTitle");
        h4.innerHTML = book.title;
    
        var catalogueBox = document.getElementById("catalogue");
    
        var a = document.createElement("a");
    
        a.href = "form.htm";
    
        a.style.backgroundImage = `url(${book.image})`;
    
        a.appendChild(h4);
        catalogueBox.appendChild(a);
    
        a.addEventListener("click", function(event) {
            event.preventDefault();
            var bookTitle = h4.innerHTML;
            var bookcover = book.image
            var bookingSystemTitles = { "title": bookTitle , "cover":bookcover };
            console.log(bookingSystemTitles);
            localStorage.setItem("booktitle", JSON.stringify(bookingSystemTitles));


            document.body.style.transition = "opacity 0.5s"; // Set the transition property
            document.body.style.opacity = 0; // Start the transition immediately
            
            setTimeout(function() {
                window.location.href = 'applicationForm.htm'; // Navigate to the new page after the transition
            }, 500);
            
        });
    }
    
    document.addEventListener("DOMContentLoaded", function() {
        booksArray.forEach(function(book) {
            search(book);
        });
       
    });
    
    function fullsearch() {
        var catalogueBox = document.getElementById("catalogue");
        catalogueBox.innerHTML = ""; // Clear the catalogue content
        search();
    }
    
    function search() {
        var myInput = document.getElementById("myInput");
        var filter = myInput.value.toUpperCase(); // Convert search input to uppercase for case-insensitive comparison
        var catalogueBox = document.getElementById("catalogue");
        catalogueBox.innerHTML = ""; // Clear the catalogue content
        var maxResults = 0;
    
        for (var i = 0; i < booksArray.length; i++) {
            var book = booksArray[i];
            if (book.title.toUpperCase().indexOf(filter) > -1) {
                listArray(book);
                maxResults++;
            }
            if (maxResults === 6) {
                break;
            }
        }
    }
    